import React, {useState, useEffect, useContext, useReducer, usePrevious} from 'react';
import {Container, Box, Button, Heading, Text, TextField} from 'gestalt';
export default function Signup() {
    
    return(
    <Container>
        <Box
        dangerouslySetInlineStyle={{
            __style: {
                backgroundColor: '#ebe2da'
            }
        }}
        margin={4}
        padding={4}
        shape="rounded"
        display="flex"
        justifyContent="center"
        >
        <form style={{display:'inlineBlock', textAlign:'center', maxWidth: 450}}>
            <Box marginBottom={2} display="flex" direction="column" alignItems="center" >
            <Heading color="midnight" >Let's get started</Heading>
            <Text italic color="orchid">Sign up to order some brews!</Text>
            </Box>
            <TextField id="username" type="text" name="username" placeholder="username" onChange={handleChange}/>
            <TextField id="email" type="email" name="email" placeholder="email address" onChange={handleChange}/>
            <TextField id="password" type="password" name="password" placeholder="password" onChange={handleChange}/>

            <Button color="blue" text="Submit" type="submit"/>


        </form>

        </Box>
    </Container>
    )
}
