const CART_KEY = 'cart';

export const calculatePrice = items => {
    return `$${items
        .reduce((acc, item) => acc + item.quantity * item.price, 0)
        .toFixed(2)
    }`
}

export const setCart = (value, cartKey = CART_KEY) => {
    if(localStorage) {
        localStorage.setItem(cartKey, JSON.stringify(value));
    }
}
