import uuidv4 from 'uuid';

export default function reducer(state, action, match) {
    switch(action.type){
        case "SET_USER":
            return {
                ...match,
                user: action.payload,
                name: action.payload.displayName
            }
       
        default: 
        return match;
    }
}
