import React, {useState, useEffect, useContext, useReducer, usePrevious} from 'react';
import {Container, Box, Button, Heading, Text, TextField, Toast} from 'gestalt';
import {getCart} from '../utils/index';
export default function Checkout() {
    const [toast, setToast] = useState(false)
    const [toastMessage, setToastMessage] = useState('')
    const [loading, setLoading] = useState(false)
    const [address, setAddress] = useState('')
    const [city, setCity] = useState('')
    const [zip, setZip] = useState('')
    const [cemail, setCEmail] = useState('')
    const [cartItems, setCartItems] = useState([])

    useEffect(() => {
        setCartItems(getCart())
    },[])

    const handleConfirmOrder = async event => {
        event.preventDefault();
        if(isFormEmpty()) {
            showToast('Fill in all fields!')
            return;
        }
       
        //sign up user
        try {
            setLoading(true);
        //   const response = await strapi.register(username, email, password)
          setLoading(false);
        //   setToken(response.jwt);
      
          window.location.href="/"
        }catch(err) {
          setLoading(false);
          showToast(err.message)
        }
    }

    const isFormEmpty = () => {
        return !address || !zip || !city || !cemail
      }
    
      const showToast = (tm) => {
        setToast(true)
        setToastMessage(tm)
        setTimeout(() => setToast(false), 5000)
      }
    return(
    
        <Container>
        <Box
        color="darkWash"
        margin={4}
        padding={4}
        shape="rounded"
        display="flex"
        justifyContent="center"
        alignItems="center"
        direction="column"
        >
        <Heading color="midnight" >Checkout</Heading>
        <form onSubmit={handleConfirmOrder} style={{display:'inlineBlock', textAlign:'center', maxWidth: 450}}>
            
            <Box>
            <TextField id="address" name="address" type="text" placeholder="shipping address"  onChange={event => setAddress(event.value)}/>
            <TextField id="postalCode" type="number" name="postalCode" placeholder="Zip Code" onChange={event => setZip(event.value)}/>
            <TextField id="city" type="text" name="city" placeholder="password" onChange={event => setCity(event.value)}/>
            <TextField id="confirmationEmailAddress" type="email" name="confirmationEmailAddress" placeholder="Confirmation Email Address" onChange={event => setCEmail(event.value)}/>
            <button id="stripe__button" type="submit">Submit</button>
            </Box>

        </form>

        </Box>
       {toast ? (
       <Box
       dangerouslySetInlineStyle={{
       __style: {
           bottom:250,
           left:"50%",
           transform: "translateX(-50%)"
       }
       }}
       position="fixed"
       >
       <Toast color="orange" text={toastMessage}  />
       </Box>
       ) : ('')} 
 
    </Container>
      
    )
}
